function hasClass(e,n){return new RegExp(" "+n+" ").test(" "+e.className+" ")}function toggleClass(e,n){var s=" "+e.className.replace(/[\t\r\n]/g," ")+" ";if(hasClass(e,n)){for(;s.indexOf(" "+n+" ")>=0;)s=s.replace(" "+n+" "," ");e.className=s.replace(/^\s+|\s+$/g,"")}else e.className+=" "+n}document.addEventListener("DOMContentLoaded",function(){document.getElementById("menu-link").onclick=function(){toggleClass(document.body,"active")}});