//arrow navigation
$(document).ready(function(){$(document).keydown(function(e){var t=false;if(e.which==37){t=$('a[rel="prev"]').attr("href")}else if(e.which==39){t=$('a[rel="next"]').attr("href")}if(t){window.location=t}})})

//smooth scroll
$("a[href*=#]:not([href=#])").click(function(){if(location.pathname.replace(/^\//,"")==this.pathname.replace(/^\//,"")||location.hostname==this.hostname){var e=$(this.hash);e=e.length?e:$("[name="+this.hash.slice(1)+"]");if(e.length){$("html,body").animate({scrollTop:e.offset().top},1e3);return false}}})
